import React, { useState } from "react";
import { makeStyles } from "@material-ui/styles";
import { Box, Button, Typography, Card, CardContent } from "@material-ui/core";
import DescriptionOutlinedIcon from "@material-ui/icons/DescriptionOutlined";

const useStyles = makeStyles((theme) => ({
  infobox: {
    position: "absolute",
    left: "28vw",
    marginTop: "60px",
    zIndex: 1000000000000000,
    flexDirection: "column",
    width: "12vw",
    padding: theme.spacing(2),
    backgroundColor: " #fff",
    borderRadius: 5,
    display: "flex",
    alignItems: "center",
    boxShadow: "0 0 15px rgba(10,40,100,.1)",
    cursor: "pointer",
  },
  icon: {
    padding: "10px",
  },
  apply: {
    backgroundColor: "#f4d47d",
  },
}));

const ApplyButton = () => {
  const [isShown, setIsShown] = useState(false);
  const classes = useStyles();

  return (
    <React.Fragment>
      <Box
        className={classes.apply}
        onMouseEnter={() => setIsShown(true)}
        onMouseLeave={() => setIsShown(false)}
      >
        Envoyer ma candidature <DescriptionOutlinedIcon />
      </Box>
      {isShown && (
        <Card className={classes.infobox} id="apply-button-infobox">
          <CardContent>
            <Typography variant="subtitle2" color="primary">
              <Box className={classes.icon}>
                <DescriptionOutlinedIcon color="action" fontSize="large" />
              </Box>
              Soumettre ma candidature directement Ã  l'entreprise
            </Typography>
          </CardContent>
        </Card>
      )}
    </React.Fragment>
  );
};

export default ApplyButton;
