import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import { Paper, Grid, Typography, CardMedia } from "@material-ui/core";
import CompanyLogo from "./CompanyLogo";
import JobDescription from "./JobDescription";

const useStyles = makeStyles((theme) => ({
  spacing: 8,

  root: {
    flexGrow: 1,
  },
  paper: {
    padding: theme.spacing(2),
    textAlign: "justify",
    color: theme.palette.text.primary,
  },
  text: {
    color: theme.palette.text.secondary,
    padding: theme.spacing(2),
  },
  h5: {
    color: theme.palette.text.primary,
    padding: theme.spacing(2),
  },
  cardMedia: {
    padding: -2,
    maxWidth: "5rem",
  },
  paperGrid: {
    display: "flex",
    flexFlow: "row no-wrap",
  },
}));

const ApplyRow = ({ applyRow }) => {
  const classes = useStyles();

  return (
    <Grid item xs={15} sm={12} className={classes.root}>
      <Grid container className={classes.paperGrid} spacing={0}>
        <Paper className={classes.paper}>
          <Grid item xs={6} sm={3} className={classes.root}></Grid>
          <Grid item xs={15} sm={12} className={classes.root}>
            <CardMedia className={classes.cardMedia}>
              <CompanyLogo logo={applyRow.imgUrl} />
            </CardMedia>
            <JobDescription jobDescription={applyRow.jobDescription} />
          </Grid>
        </Paper>
      </Grid>
    </Grid>
  );
};
export default ApplyRow;
