import React, { useState } from "react";
import { makeStyles } from "@material-ui/core/styles";
import {
  Paper,
  Grid,
  Typography,
  CardMedia,
  Container,
  Button,
} from "@material-ui/core";
import CompanyLogo from "./CompanyLogo";
import JobDescription from "./JobDescription";
import ApplyButton from "./ApplyButton";

const useStyles = makeStyles((theme) => ({
  spacing: 8,

  root: {
    flexGrow: 1,
  },
  text: {
    color: theme.palette.text.secondary,
    padding: theme.spacing(2),
  },
  title: {
    color: theme.palette.text.primary,
    padding: theme.spacing(2),
  },
  logo: {
    padding: theme.spacing(2),
    maxWidth: "4rem",
  },
  paperGrid: {
    display: "flex",
    flexDirection: "row",
    alignItems: "flex-start",
  },
  cardMedia: {
    height: "20rem",
    margin: "0 3rem 6rem 0",
    padding: "3rem",
  },

  titleContainer: {
    display: "flex",
    flexDirection: "row",
    alignItems: "flex-start",
  },
}));

const ApplyRow = ({ applyRow }) => {
  const classes = useStyles();

  return (
    <Grid item xs={24} sm={21} className={classes.root}>
      <Container className={classes.paperGrid} maxWidth="lg">
        <Grid
          item
          xs={12}
          sm={9}
          className={classes.root}
          className={classes.squareArea}
        >
          <Container className={classes.titleContainer} maxWidth="md">
            <Button className={classes.logo} variant="outlined" color="primary">
              {" "}
              <CompanyLogo logo={applyRow} />
            </Button>
            <Typography className={classes.title} gutterBottom variant="h5">
              Postuler maintenant Ã  {applyRow.company}
            </Typography>
          </Container>

          <CardMedia
            className={classes.cardMedia}
            image="/images/colleagues.svg"
            title="Shared Workspace"
          />
          <ApplyButton />
        </Grid>
        <Grid
          className={classes.squareArea}
          item
          xs={12}
          sm={9}
          className={classes.root}
        >
          <JobDescription jobDescription={applyRow.jobDescription} />
        </Grid>
      </Container>
    </Grid>
  );
};
export default ApplyRow;
