import React, { useState } from "react";
import { makeStyles } from "@material-ui/styles";
import {
  Box,
  Button,
  Typography,
  Card,
  CardContent,
  Fab,
} from "@material-ui/core";
import DescriptionOutlinedIcon from "@material-ui/icons/DescriptionOutlined";
import OverlayCompany from "../OverlayCompany";

const useStyles = makeStyles((theme) => ({
  candidater: {
    backgroundColor: "#f4d47d",
    color: theme.palette.text.secondary,
    textTransform: "none",
    pointerEvents: "pointer",
  },
  infobox: {
    position: "absolute",
    left: "28vw",
    marginTop: "60px",
    zIndex: 1000000000000000,
    flexDirection: "column",
    width: "12vw",
    padding: theme.spacing(2),
    backgroundColor: " #fff",
    borderRadius: 5,
    display: "flex",
    alignItems: "center",
    boxShadow: "0 0 15px rgba(10,40,100,.1)",
    cursor: "pointer",
  },
  icon: {
    padding: "10px",
  },
}));

const ApplyButton = () => {
  const [isShown, setIsShown] = useState(false);
  const classes = useStyles();

  return (
    <React.Fragment>
      <Fab
        variant="extended"
        className={classes.candidater}
        onMouseClick={() => setIsShown(true)}
      >
        Envoyer ma candidature <DescriptionOutlinedIcon />
      </Fab>
      {isShown && <OverlayCompany />}
    </React.Fragment>
  );
};

export default ApplyButton;
