import React, { useState } from "react";
import { makeStyles } from "@material-ui/styles";
import {
  Box,
  Button,
  Typography,
  Card,
  CardContent,
  Fab,
  Container,
} from "@material-ui/core";
import DescriptionOutlinedIcon from "@material-ui/icons/DescriptionOutlined";

const useStyles = makeStyles((theme) => ({
  overlay: {
    position: "fixed",
    top: 0,
    bottom: 0,
    minWidth: "100vw",
    backgroundColor: "rgba(10,40,100,0.9)",
  },
  pannel: {
    position: "fixed",
    left: "28vw",
    marginTop: "60px",
    zIndex: 1000000000000000,
    width: "32vw",
    padding: theme.spacing(2),
    backgroundColor: " #fff",
    borderRadius: 5,
    display: "flex",
    alignItems: "center",
    flexDirection: "column",
    boxShadow: "0 0 15px rgba(10,40,100,.1)",
  },
  icon: {
    padding: "10px",
  },
}));

const OverlayCompany = () => {
  const [isShown, setIsShown] = useState(false);
  const classes = useStyles();

  return (
    <React.Fragment>
      <Box className={classes.overlay}>
        <Container className={classes.pannel} maxWidth="md">
          <Fab
            variant="extended"
            className={classes.candidater}
            onMouseEnter={() => setIsShown(true)}
            onMouseLeave={() => setIsShown(false)}
          >
            Telecharger <DescriptionOutlinedIcon />
          </Fab>
          <Fab
            variant="extended"
            className={classes.candidater}
            onMouseEnter={() => setIsShown(true)}
            onMouseLeave={() => setIsShown(false)}
          >
            Annuler <DescriptionOutlinedIcon />
          </Fab>
        </Container>
      </Box>
    </React.Fragment>
  );
};

export default OverlayCompany;
