import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import { Paper, Grid, Typography, CardMedia } from "@material-ui/core";
import CompanyLogo from "./CompanyLogo";
import JobDescription from "./JobDescription";

const useStyles = makeStyles((theme) => ({
  spacing: 8,

  root: {
    flexGrow: 1,
  },
  paper: {
    padding: theme.spacing(2),
    textAlign: "justify",
    color: theme.palette.text.primary,
  },
  text: {
    color: theme.palette.text.secondary,
    padding: theme.spacing(2),
  },
  h5: {
    color: theme.palette.text.primary,
    padding: theme.spacing(2),
  },
  cardMedia: {
    padding: -2,
    maxWidth: "5rem",
  },
}));

const ApplyRow = ({ applyRow }) => {
  const classes = useStyles();

  return (
    <Grid item xs={15} sm={12} className={classes.root}>
      <Paper className={classes.paper}>
        <CardMedia className={classes.cardMedia}>
          <CompanyLogo logo={applyRow.imgUrl} />
        </CardMedia>
        <JobDescription jobDescription={applyRow.jobDescription} />
      </Paper>
    </Grid>
  );
};
export default ApplyRow;
