import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import {
  Paper,
  Grid,
  Typography,
  CardMedia,
  Container,
  Button,
  TextareaAutosize,
} from "@material-ui/core";

const useStyles = makeStyles((theme) => ({
  spacing: 8,

  root: {
    flexGrow: 1,
  },
  text: {
    color: theme.palette.text.secondary,
    padding: theme.spacing(2),
  },
  title: {
    color: theme.palette.text.primary,
    padding: theme.spacing(1),
  },
  logo: {
    padding: theme.spacing(2),
    maxWidth: "4rem",
  },
  paperGrid: {
    display: "flex",
    flexDirection: "row",
    alignItems: "flex-start",
    marginBottom: theme.spacing(20),
  },
  cardMedia: {
    minHeight: "17rem",
    maxHeight: "40rem",
    marginBottom: theme.spacing(4),
    padding: theme.spacing(2),
  },
  titleContainer: {
    display: "flex",
    flexDirection: "row",
    alignItems: "flex-start",
  },
  squareArea: {
    display: "flex",
    flexFlow: "column wrap",
    padding: theme.spacing(3),
    margin: theme.spacing(2),
    height: "85vh",
  },
  blue: {
    backgroundColor: "rgba(10,40,100,0.9)",
    padding: theme.spacing(3),
    margin: theme.spacing(2),
    height: "85vh",
  },
}));

const Chat = ({ applyRow }) => {
  const classes = useStyles();

  return (
    <Container className={classes.paperGrid} maxWidth="lg">
      <Paper className={classes.squareArea}>
        <Container className={classes.titleContainer} maxWidth="md">
          <Typography className={classes.title} gutterBottom variant="h3">
            Vos conversations
          </Typography>
        </Container>
        <TextareaAutosize
          aria-label="minimum height"
          rowsMin={3}
          placeholder="Minimum 3 rows"
        />

        <Button className={classes.logo} variant="outlined" color="primary">
          Envoyer
        </Button>
      </Paper>
    </Container>
  );
};
export default Chat;
