import React from "react";
import { makeStyles } from "@material-ui/core/styles";
import {
  Paper,
  Grid,
  Typography,
  CardMedia,
  Container,
  Button,
} from "@material-ui/core";
import Chat from "./Chat";
import ChatButton from "./ChatButton";

const useStyles = makeStyles((theme) => ({
  spacing: 8,

  root: {
    flexGrow: 1,
  },
  text: {
    color: theme.palette.text.secondary,
    padding: theme.spacing(2),
  },
  title: {
    color: theme.palette.text.primary,
    padding: theme.spacing(1),
  },
  logo: {
    padding: theme.spacing(2),
    maxWidth: "4rem",
  },
  paperGrid: {
    display: "flex",
    flexDirection: "row",
    alignItems: "flex-start",
    marginBottom: theme.spacing(20),
  },
  cardMedia: {
    minHeight: "17rem",
    maxHeight: "40rem",
    marginBottom: theme.spacing(4),
    padding: theme.spacing(2),
  },
  titleContainer: {
    display: "flex",
    flexDirection: "row",
    alignItems: "flex-start",
  },
  squareArea: {
    padding: theme.spacing(3),
    margin: theme.spacing(2),
    height: "85vh",
  },
  blue: {
    backgroundColor: "rgba(10,40,100,0.9)",
    padding: theme.spacing(3),
    margin: theme.spacing(2),
    height: "85vh",
  },
}));

const ChatRow = ({}) => {
  const classes = useStyles();

  return (
    <Grid item xs={24} sm={21} className={classes.root}>
      <Container className={classes.paperGrid} maxWidth="lg">
        <Grid item xs={18} sm={12} className={classes.root}>
          <Paper className={classes.squareArea}>
            <Container className={classes.titleContainer} maxWidth="md">
              <Card className={classes.card}>
                <Button
                  className={classes.logo}
                  variant="outlined"
                  color="primary"
                ></Button>
                <Typography className={classes.title} gutterBottom variant="h3">
                  Des questions?
                </Typography>
                <CardMedia
                  className={classes.cardMedia}
                  image={job.imgUrl}
                  title={job.title}
                />
                <CardContent className={classes.cardContent}>
                  <Typography gutterBottom variant="h5" component="h2">
                    {job.title}
                  </Typography>
                </CardContent>
                <CardActions>
                  <Button size="small" color="secondary" disabled>
                    <Icon>{job.icon}</Icon> {job.caption}
                  </Button>
                </CardActions>
              </Card>
            </Container>
            <CardMedia
              className={classes.cardMedia}
              image="/images/chat.png"
              title="Chat Entreprise"
            />
            <Typography className={classes.title} gutterBottom variant="h3">
              Des questions?
            </Typography>
            <ChatButton />
          </Paper>
        </Grid>

        <Grid item xs={9} sm={6} className={classes.root}>
          <Chat />
        </Grid>
      </Container>
    </Grid>
  );
};
export default ChatRow;
