import React, { useState } from "react";
import { makeStyles } from "@material-ui/styles";
import { Box, Fab, Typography } from "@material-ui/core";
import DescriptionOutlinedIcon from "@material-ui/icons/DescriptionOutlined";

const useStyles = makeStyles((theme) => ({
  candidater: {
    backgroundColor: "#f4d47d",
    color: theme.palette.text.secondary,
    textTransform: "none",
    pointerEvents: "pointer",
  },
  infobox: {
    border: "1px #eee solid",
    backgroundColor: "#eee",
    filer: "opacity(0.5)",
    color: theme.palette.text.secondary,
    textTransform: "none",
  },
}));

const ApplyButton = () => {
  const [isShown, setIsShown] = useState(false);
  const classes = useStyles();

  function handleClick(e) {
    e.preventDefault();
    console.log("The link was clicked.");
  }

  return (
    <React.Fragment>
      <Box>
        <Fab
          variant="extended"
          className={classes.candidater}
          onMouseEnter={() => setIsShown(true)}
          onMouseLeave={() => setIsShown(false)}
          onClick={handleClick}
        >
          Envoyer ma candidature <DescriptionOutlinedIcon />
        </Fab>
      </Box>
      {isShown && (
        <Box className={classes.infobox} id="apply-button-infobox">
          <Typography color="secondary">
            Soumettre ma candidature directement Ã  l'entreprise
          </Typography>
        </Box>
      )}
    </React.Fragment>
  );
};

export default ApplyButton;
