import React, { useState } from "react";
import { makeStyles } from "@material-ui/core/styles";
import { Modal } from "@material-ui/core";
import CompanyLogo from "../CompanyLogo";

const useStyles = makeStyles((theme) => ({
  paper: {
    position: "absolute",
    width: "32vw",
    backgroundColor: theme.palette.background.paper,
    borderRadius: 5,
    padding: theme.spacing(2, 4, 3),
    boxShadow: "0 0 15px rgba(10,40,100,0.1)",
  },
  overlay: {
    position: "fixed",
    top: 0,
    left: 0,
    bottom: 0,
    minWidth: "100vw",
    backgroundColor: "rgba(10,40,100,0.9)",
  },
}));

const OverlayCompanyProfile = ({ info }) => {
  const classes = useStyles();
  const [open, setOpen] = useState(false);

  const handleOpen = () => {
    setOpen(true);
  };
  const handleClose = () => {
    setOpen(false);
  };

  const body = (
    <div className={classes.paper}>
      <h2>{info.company}</h2>
      <p>{info.companyDescription}</p>
      <OverlayCompanyProfile />
    </div>
  );

  return (
    <Modal
      open={open}
      onClose={handleClose}
      aria-labelledby="Company Profile"
      aria-describedby="Company-description"
    >
      {body}
    </Modal>
  );
};

export default OverlayCompanyProfile;
