import React, { useState } from "react";
import { CardMedia } from "@material-ui/core";
import { OverlayCompanyProfile } from "./OverlayCompanyProfile";

const CompanyLogo = ({ logo }) => {
  const [open, setOpen] = useState(false);
  const handleOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  return (
    <React.Fragment>
      <CardMedia
        onClick={handleOpen}
        id="company-avatar"
        component="img"
        alt="company-image"
        max-height="45"
        max-width="30"
        image={logo.imgUrl}
        title="Marine Logo"
      />
      {open && <CompanyProfileModal onClose={handleClose} />}
    </React.Fragment>
  );
};

export default CompanyLogo;

const ApplyButton = () => {
  const [isShown, setIsShown] = useState(false);
  const classes = useStyles();
  const closeModal = () => {
    setIsShown(false);
  };

  return (
    <React.Fragment>
      <Fab
        variant="extended"
        className={classes.candidater}
        onClick={() => setIsShown(true)}
      >
        Envoyer ma candidature <DescriptionOutlinedIcon />
      </Fab>
      {isShown && <OverlayApply closeModal={closeModal} />}
    </React.Fragment>
  );
};
