import React from "react";
import { Card, Container, Button, Grid, ButtonGroup } from "@material-ui/core";
import ApplyButton from "./JobProfileElements/ApplyButton";
import Pills from "./JobProfileElements/Pills";
import JobLinks from "./JobProfileElements/JobLinks";
import CompanyBanner from "./JobProfileElements/CompanyBanner";
import ChatOption from "./JobProfileElements/ChatOption";
import CompanyLogo from "./JobProfileElements/CompanyLogo";
import { makeStyles } from "@material-ui/styles";
import Booking from "./JobProfileElements/Booking";

const useStyles = makeStyles((theme) => ({
  buttonGroup: {
    margin: "3rem",
    alignItems: "center",
    textTransform: "none",
    padding: "5px",
    boxShadow: "0 0 15px rgba(10,40,100,.1)",
  },
  profileCard: {
    padding: "1em",
    backgroundImage: "url(/images/marine.png)",
    overflow: "hidden",
    backgroundPosition: "center",
    backgroundSize: "cover",
    backgroundRepeat: "no-repeat",
    minHeight: "45vh",
  },
  buttons: {
    marginRight: 0,
    backgroundColor: "rgba(255,255,255)",
    padding: "1em 3em",
  },
  apply: {
    backgroundColor: "#f4d47d",
    marginRight: 0,
    padding: "1em 3em",
  },
  card: {
    maxWidth: "4rem",
  },
}));

const JobProfile = ({ jobProfile }) => {
  const classes = useStyles();
  return (
    <Grid item xs={18} sm={12}>
      <Card className={classes.profileCard}>
        <CompanyBanner titles={jobProfile.titles} />
        <JobLinks />
        <Pills pills={jobProfile.pills} />
      </Card>
      <Card className={classes.card}>
        <CompanyLogo logo={jobProfile.imgUrl} />
      </Card>

      <ButtonGroup
        className={classes.buttonGroup}
        size="large"
        color="primary"
        aria-label="apply"
      >
        <Button className={classes.apply}>
          <ApplyButton />
        </Button>
        <Button className={classes.buttons}>
          <ChatOption />
        </Button>
        <Button className={classes.buttons}>
          <Booking />
        </Button>
      </ButtonGroup>
    </Grid>
  );
};

export default JobProfile;
