import React, { useState } from "react";
import { CardMedia } from "@material-ui/core";

const CompanyLogo = ({ logo }) => {
  const [open, setOpen] = useState(false);
  const handleOpen = () => {
    setOpen(true);
  };

  const handleClose = () => {
    setOpen(false);
  };

  return (
    <React.Fragment>
      <CardMedia
        onClick={handleOpen}
        id="company-avatar"
        component="img"
        alt="company-image"
        max-height="45"
        max-width="30"
        image={logo.imgUrl}
        title="Marine Logo"
      />
      {/* {open && <OverlayCompanyProfile info={logo} onClose={handleClose} />} */}
    </React.Fragment>
  );
};

export default CompanyLogo;
