import React from "react";
import JobProfile from "./JobProfile";
import BreadCrumbs from "./BreadCrumbs";
import Sider from "./Sider";
import JobDescription from "./JobDescription";
import Otherjobs from "./OtherJobs";
import JobImages from "./JobImages";
import { makeStyles } from "@material-ui/core/styles";
import { Grid, Container } from "@material-ui/core";

const CandidatePage = () => {
  const data = [
    {
      jobDescription:
        "The Joint Directorate of Infrastructure Networks and Information Systems  is the operator of information and communication systems of the Armed Forces. The Service Desk (SDK) is the single entry point for service requests (incident and / or catalog request) from subscribers of the Ministry of the Armed Forces. Physically distributed over four geographic sites (Maisons-Laffitte, Metz, Rennes and Toulon), this single, global entity has processing capacity throughout the metropolis and relies on “physical” DIRISI service centers , multi-located on the national territory. The SDK provides a 24/7 service and makes every effort to improve the quality of service offered by DIRISI to the various departments and departments of the ministry. Main activities : The incumbent is responsible for the conduct-coordination unit of the Ile de France SDK located in Maisons-Laffitte, he ensures a transverse role while ensuring coordination with all internal and external actors. In charge of running the daily briefing, he ensures: - Cross-functional management between FO (Front Office) and BO (Back Office); - The correct application of internal directives and those related to SMS (safety management system); - Coordination with other SDKs as well as with other industrial actors or technicians of DIRISI (CNMO  national implementation center ; COD  DIRISI operational center , CIRISI  joint center for infrastructure networks and information systems . Responsible for managing local indicators with the back office",

      jobProfile: [
        {
          titles: {
            jobTitle: "Coordinateur service informatique",
            companyTitle: "Little Simz",
            imgUrl: "/images/logo-marine.png",
          },
        },
      ],
    },
  ];

  return (
    <React.Fragment>
      <BreadCrumbs />
      <Container maxWidth="lg">
        <Grid container spacing={2}>
          <JobProfile jobProfile={data.jobProfile} />
          <Sider />
          <JobDescription jobDescription={data.jobDescription} />
        </Grid>
        <JobImages />
        <Otherjobs />
      </Container>
    </React.Fragment>
  );
};

export default CandidatePage;
