import React, { useState } from "react";
import { makeStyles } from "@material-ui/core/styles";
import {
  Paper,
  Grid,
  Typography,
  CardMedia,
  Container,
  Card,
  Button,
} from "@material-ui/core";
import CompanyLogo from "./CompanyLogo";
import JobDescription from "./JobDescription";
import ApplyButton from "./ApplyButton";

const useStyles = makeStyles((theme) => ({
  spacing: 8,

  root: {
    flexGrow: 1,
  },
  paper: {
    padding: theme.spacing(2),
    textAlign: "justify",
    color: theme.palette.text.primary,
  },
  text: {
    color: theme.palette.text.secondary,
    padding: theme.spacing(2),
  },
  title: {
    color: theme.palette.text.primary,
    padding: theme.spacing(2),
  },
  logo: {
    padding: -2,
    maxWidth: "3rem",
  },
  paperGrid: {
    display: "flex",
    flexDirection: "row",
    alignItems: "flex-start",
  },
  cardMedia: {
    height: "20rem",
    margin: "0 3rem 6rem 0",
    padding: "3rem",
  },
  buttonArea: {
    margin: "3rem",
  },
  titleContainer: {
    display: "flex",
    flexDirection: "row",
    alignItems: "flex-start",
  },
}));

const ApplyRow = ({ applyRow }) => {
  const classes = useStyles();
  const [isShown, setIsShown] = useState(false);

  return (
    <Grid item xs={24} sm={21} className={classes.root}>
      <Paper className={classes.paper}>
        <Container className={classes.paperGrid} maxWidth="lg">
          <Grid
            item
            xs={12}
            sm={9}
            className={classes.root}
            className={classes.buttonArea}
          >
            <Container className={classes.titleContainer} maxWidth="sm">
              <Button
                // onClick={() => setIsShown(true)}
                variant="outlined"
                color="primary"
              >
                <CardMedia className={classes.logo}>
                  <CompanyLogo logo={applyRow.imgUrl} />
                </CardMedia>
              </Button>
              <Typography className={classes.title} gutterBottom variant="h4">
                Postuler maintenant
              </Typography>
            </Container>

            <CardMedia
              className={classes.cardMedia}
              image="/images/colleagues.svg"
              title="Shared Workspace"
            />
            <ApplyButton />
          </Grid>
          <Grid item xs={15} sm={12} className={classes.root}>
            <JobDescription jobDescription={applyRow.jobDescription} />
          </Grid>
        </Container>
      </Paper>
    </Grid>
  );
};
export default ApplyRow;
